def load_data(file, default):
    if os.path.exists(file):
        with open(file, "r", encoding="utf-8") as f: 
            return json.load(f)
    return default

def save_data(file, data):
    with open(file, "w", encoding="utf-8") as f:
        json.dump(data, f, ensure_ascii=False, indent=4)

